        methods: {
            loginUser () {
                const me = this
                let token = me.$cookies.get('70hokc3hhhn5')
                if (token) {
                    me.$router.push('/my-profile')
                } else {
                    me.$store.state.loginSignUpStatus = true
                    document.body.classList.add('no_scroll')
                }
            },
            openGallery(key) {
                const me = this
                // me.imagesToSend = me.parser(me.studioImages[key].images)
                me.imagesToSend = me.studioImages[key].images
                me.showGallery = true
                setTimeout( () => {
                    me.$refs.gallery.opened = true
                }, 200)
                document.body.classList.add('no_scroll')
            },
            toggleStep (key, data) {
                const me = this
                me.currentStep = `step_${key + 1}`
                me.getSetSteps.forEach((value, index) => {
                    if (index != key) {
                        let element = document.getElementById(`step_${index + 1}`)
                        element.querySelector('.description').style.height = `0px`
                        value.toggled = false
                    } else {
                        let element = document.getElementById(`step_${index + 1}`)
                        element.querySelector('.description').style.height = `${element.querySelector('.description').scrollHeight}px`
                        value.toggled = true
                    }
                })
                me.toggled = true
                setTimeout( () => {
                    me.$scrollTo('.bike_wrapper', {
                        offset: -100
                    })
                }, 250)
            },
            toggleActionHover (key) {
                const me = this
                let element = document.getElementById(`item_${key}`)
                element.querySelector('.action').style.height = `${element.querySelector('.action').scrollHeight}px`
            },
            toggleActionLeave (key) {
                const me = this
                let element = document.getElementById(`item_${key}`)
                element.querySelector('.action').style.height = `0px`
            },
            async initial () {
                const me = this
                me.loader(true)
                setTimeout( () => {
                    me.loaded = true
                    me.loader(false)
                }, 500)
            }
        },
        async mounted () {
            const me = this
            await setTimeout( () => {
                me.initial()
            }, 10)
        },
        asyncData ({ $axios, params, error, store }) {
            return $axios.get(`api/web/riders-guide`)
            .then(res => {
                return {
                    res: res.data.pageSetting,
                    instructors: res.data.instructors,
                    studioImages: res.data.studioAlbums
                }
            }).catch(err => {
                error({ statusCode: 403, message: 'Page not found' })
            })
        },
        head () {
            const me = this
            let host = process.env.baseUrl
            return {
                title: `${me.res.title} | Ride Revolution`,
                link: [
                    {
                        rel: 'canonical',
                        href: `${host}${me.$route.fullPath}`
                    }
                ],
                meta: [
                    { hid: 'og:title', property: 'og:title', content: `${me.res.meta_title}` },
                    { hid: 'og:description', property: 'og:description', content: `${me.res.meta_description}` },
                    { hid: 'og:keywords', property: 'og:keywords', content: `${me.res.meta_keywords}` },
                    { hid: 'og:url', property: 'og:url', content: `${host}/${me.$route.fullPath}` },
                    { hid: 'og:image', property: 'og:image', content: `${me.res.banners[0].path}` },
                    { hid: 'og:image:alt', property: 'og:image:alt', content: `${me.res.banners[0].alt}` },
                    { hid: 'og:type', property: 'og:type', content: 'website' },
                    { hid: 'og:site_name', property: 'og:site_name', content: 'Ride Revolution' },
                ]
            }
        }
    }
